let timeline = gsap.timeline({repeat: -1, yoyo: true})

timeline.to('h1', {duration:1, x: 300, y:200, rotation:10, delay:1});
timeline.to('.box', {duration:1, x: 300, y:200, rotation:360, backgroundColor: "#bdbdbd", borderRadius: "20%", ease: "front"});

timeline.from('li', {duration:1, x:"-300px", opacity: 0, stagger: .5, delay:1});
